class Solution:
    def findLongestWord(self, s, d):
        """
        :type s: str
        :type d: List[str]
        :rtype: str
        """

        def is_sub(w, s):
            i = j = 0
            while i < len(w) and j < len(s):
                if w[i] == s[j]:
                    i += 1
                    j += 1
                else:
                    j += 1
            if i == len(w):
                return True
            else:
                return False

        best = ""
        for word in d:
            if is_sub(word, s):
                if len(word) > len(best) or (len(word) == len(best)
                                             and word < best):
                    best = word
        return best


fn = Solution().findLongestWord

print(fn(s="abpcplea", d=["ale", "apple", "monkey", "plea"]))
print(fn(s="abpcplea", d=["a", "b", "c"]))
print(fn("bab", ["ba", "a", "b", "ab"]))

print(
    fn("",
       [
           "",
           "",
           "",
           "",
           "",
           "",
           "",
           "",
           "",
           ""
       ]))
