#!/usr/bin/env python
# coding: utf-8

# p680

class Solution:
    def validPalindrome(self, s):
        """
        :type s: str
        :rtype: bool
        """
        i, j = 0, len(s) - 1
        while i < j:
            if s[i] == s[j]:
                i += 1
                j -= 1
            else:
                r1 = s[i + 1:j + 1]
                r2 = s[i:j]
                return r1 == r1[::-1] or r2 == r2[::-1]
        else:
            return True


if __name__ == '__main__':
    sol = Solution()
    print(sol.validPalindrome("aba"))
    print(sol.validPalindrome("abca"))
    print(sol.validPalindrome("ebcbbececab"
                              "bacecbbcbe"))

    print(sol.validPalindrome("eccer"))
    print(sol.validPalindrome("recce"))

    print(sol.validPalindrome("abcda"))
    print(sol.validPalindrome(
        ""))
